const SearchSuggestionSystem = () => {
  type Maybe<T> =
    | {
        tag: 'Just';
        value: T;
      }
    | {
        tag: 'Nothing';
      };

  const just = <T>(x: T): Maybe<T> => ({ tag: 'Just', value: x });

  const nothing = <T>(): Maybe<T> => ({ tag: 'Nothing' });

  const isJust = <T>(mb: Maybe<T>) => mb.tag === 'Just';

  const fromMaybe = <T>(defv: T, mb: Maybe<T>): T =>
    mb.tag === 'Nothing' ? defv : mb.value;

  type Trie = {
    children: Record<string, Trie>;
    value: Maybe<string>;
  };

  const addWordToTrie = (t: Trie, s: string): Trie => {
    const added = s.split('').reduce((t1: Trie, c: string): Trie => {
      if (t1.children[c] === undefined) {
        Object.assign(t1.children, { [c]: { children: [], value: nothing() } });
      }
      return t1.children[c];
    }, t);
    Object.assign(added, { value: just(s) });
    return t;
  };

  const mkTrie = (xs: string[]): Trie => {
    const q: Trie = xs.reduce(addWordToTrie, {
      children: {},
      value: nothing(),
    });
    return q;
  };

  const firstN = (n: number) => (t: Trie): string[] => {
    const helper = (t1: Trie, res: string[]): string[] => {
      if (res.length >= n) {
        return res;
      }
      if (isJust(t1.value)) {
        res.push(fromMaybe('', t1.value));
      }
      return Object.keys(t1.children)
        .sort()
        .reduce((res1, key) => helper(t1.children[key], res1), res);
    };
    return helper(t, []);
  };

  type SPState = {
    trie: Trie;
    output: Trie[];
  };

  const mkSPState = (products: string[]): SPState => ({
    trie: mkTrie(products),
    output: [],
  });

  const suggestedProductsStep = (s: SPState, c: string): SPState =>
    s.trie.children[c] === undefined
      ? { trie: mkTrie([]), output: s.output.concat(mkTrie([])) }
      : {
          trie: s.trie.children[c],
          output: s.output.concat(s.trie.children[c]),
        };

  const suggestedProducts = (
    products: string[],
    searchWord: string,
  ): string[][] =>
    searchWord
      .split('')
      .reduce(suggestedProductsStep, mkSPState(products))
      .output.map(firstN(3));

  console.log(
    suggestedProducts(
      ['mobile', 'mouse', 'moneypot', 'monitor', 'mousepad'],
      'mouse',
    ),
    [
      ['mobile', 'moneypot', 'monitor'],
      ['mobile', 'moneypot', 'monitor'],
      ['mouse', 'mousepad'],
      ['mouse', 'mousepad'],
      ['mouse', 'mousepad'],
    ],
  );

  console.log(suggestedProducts(['havana'], 'havana'), [
    ['havana'],
    ['havana'],
    ['havana'],
    ['havana'],
    ['havana'],
    ['havana'],
  ]);

  console.log(
    suggestedProducts(['bags', 'baggage', 'banner', 'box', 'cloths'], 'bags'),
    [
      ['baggage', 'bags', 'banner'],
      ['baggage', 'bags', 'banner'],
      ['baggage', 'bags'],
      ['bags'],
    ],
  );

  console.log(suggestedProducts(['havana'], 'tatiana'), [
    [],
    [],
    [],
    [],
    [],
    [],
    [],
  ]);

  console.log(
    suggestedProducts(
      [
        '',
        '',
        '',
        '',
      ],
      '',
    ),
  );
};

SearchSuggestionSystem();
