export const removeElementAtIndex = <T>(xs: T[], i: number): T[] =>
  xs.slice(0, i).concat(xs.slice(i + 1));

export const allPermutations = <T>(xs: T[]): T[][] => {
  if (xs.length === 0) {
    return [];
  }
  if (xs.length === 1) {
    return [xs];
  }
  return xs
    .map((x, i) =>
      allPermutations(removeElementAtIndex(xs, i)).map((ys) => [x].concat(ys)),
    )
    .flat();
};

export const mkArray = (from: number, to: number): number[] =>
  [...new Array(to - from + 1)].map((_, i) => i + from);

export const allIndicies = (word: string, pattern: string): number[] =>
  mkArray(0, word.length - 1)
    .map((i) => (word.slice(i).startsWith(pattern) ? i : -1))
    .filter((i) => i !== -1);

export const dedup = <T>(xs: T[]): T[] => Array.from(new Set(xs).values());

export function findSubstring(s: string, words: string[]): number[] {
  const perms = allPermutations(words).map((ws) => ws.join(''));
  return dedup(perms.map((w) => allIndicies(s, w)).flat());
}

